import React, { useState } from "react";
import { create } from "ipfs-http-client";
const client = create('https://ipfs.infura.io:5001/api/v0');

function App() {
  const [ipfsfile, setIpfsFile] = ("");

  const hand = async (event)=>{
    event.preventDefault();
    try {
      const upload = await client.add(file);
      const filePatch = `https://ipfs.infura.io/ipfs/${upload.path}`;
      setIpfsFile(()=>filePatch);

    }catch(err){
      console.log(err);
    }
  }
  return (
    <div className="App">
      <h1>Load file using React/IPFS</h1>
      <h2>File Path is { ipfsfile }</h2>
      <input type= "file" onChange={()=>hand()} />
    </div>
  );
}

export default App;
